#define RG_SCH_PFS_GET_PFSCELLDL(cell) \
   (RgSchPfsDlCell *)((RgSchCmnCell *)cell->sc.sch)->dl.schSpfc

#define RG_SCH_PFS_GET_PFSDL(cell) ((RgSchEnbPfsDl *)rgSchCb[cell->instIdx].rgSchEnbPfsDl)

#define RG_SCH_PFS_GET_PFSCELLUL(cell) \
   (RgSchPfsUlCell *)((RgSchCmnCell *)cell->sc.sch)->ul.schSpfc
#define RG_SCH_PFS_GET_PFSUEDL(ue, cell) \
   (RgSchPfsDlUe *)((RgSchCmnUe *)((ue->cellInfo[ue->cellIdToCellIdxMap\
                              [RG_SCH_CELLINDEX(cell)]])->sch))->dl.schSpfc

#define RG_SCH_PFS_GET_PFSUEUL(ue, cell) \
   (RgSchPfsUlUe *)((RgSchCmnUe *)((ue->cellInfo[ue->cellIdToCellIdxMap\
                              [RG_SCH_CELLINDEX(cell)]])->sch))->ul.schSpfc
