#define RG_SCH_PFS_SET_CELL_SCHD_STATE(_cell, _val)\
{\
   RgSchPfsDlCell     *pfsCellCb =  RG_SCH_PFS_GET_PFSCELLDL(_cell);\
   pfsCellCb->isFnlzDone = _val;\
}

